import React, { useState, useMemo } from 'react';

function ExpensiveComponent({ items, filter }) {
  const [count, setCount] = useState(0);

  // ‡∏Å‡∏≤‡∏£‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ‡πÄ‡∏ß‡∏•‡∏≤‡∏ô‡∏≤‡∏ô - ‡πÉ‡∏ä‡πâ useMemo ‡πÄ‡∏û‡∏∑‡πà‡∏≠ cache ‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå
  const filteredAndSortedItems = useMemo(() => {
    console.log('‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì filtered items...');
    return items
      .filter(item => item.name.toLowerCase().includes(filter.toLowerCase()))
      .sort((a, b) => a.name.localeCompare(b.name))
      .map(item => ({
        ...item,
        score: item.rating * item.reviews // ‡∏Å‡∏≤‡∏£‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì score
      }));
  }, [items, filter]); // ‡∏à‡∏∞‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡πÉ‡∏´‡∏°‡πà‡πÄ‡∏°‡∏∑‡πà‡∏≠ items ‡∏´‡∏£‡∏∑‡∏≠ filter ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô

  // ‡∏Å‡∏≤‡∏£‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì statistics
  const stats = useMemo(() => {
    const totalItems = filteredAndSortedItems.length;
    const averageScore = totalItems > 0 
      ? filteredAndSortedItems.reduce((sum, item) => sum + item.score, 0) / totalItems
      : 0;

    return {
      totalItems,
      averageScore: averageScore.toFixed(2),
      topItem: filteredAndSortedItems[0] || null
    };
  }, [filteredAndSortedItems]);

  return (
    <div>
      <h2>‡∏™‡∏ñ‡∏¥‡∏ï‡∏¥</h2>
      <p>‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£: {stats.totalItems}</p>
      <p>‡∏Ñ‡∏∞‡πÅ‡∏ô‡∏ô‡πÄ‡∏â‡∏•‡∏µ‡πà‡∏¢: {stats.averageScore}</p>

      <button onClick={() => setCount(count + 1)}>
        Count: {count} (‡πÑ‡∏°‡πà‡∏ó‡∏≥‡πÉ‡∏´‡πâ useMemo ‡∏Ñ‡∏≥‡∏ô‡∏ß‡∏ì‡πÉ‡∏´‡∏°‡πà)
      </button>

      <div>
        {filteredAndSortedItems.map(item => (
          <div key={item.id}>
            {item.name} - Score: {item.score}
          </div>
        ))}
      </div>
    </div>
  );
}

